import React, { useRef, useEffect } from "react";

import axios from "axios";

import { toast } from "react-toastify";

import { FormContainer, InputArea, Input, Label, Button } from "../styles/Forms";

export const Form = ({ onEdit }) => {
  const ref = useRef();

  useEffect(() => {
    if (onEdit) {
      const clinica = ref.current;

      clinica.CodCli.value = onEdit.CodCli;
      clinica.NomeCli.value = onEdit.NomeCli;
      clinica.Endereco.value = onEdit.Endereco;
      clinica.Telefone.value = onEdit.Telefone;
      clinica.Email.value = onEdit.Email;
    }
  })

  const handleSubmit = async (event) => {
    event.preventDefault();

    const clinica = ref.current;

    if (
      !clinica.CodCli.value ||
      !clinica.NomeCli.value ||
      !clinica.Endereco.value ||
      !clinica.Telefone.value ||
      !clinica.Email.value
    ) {
      return toast.warn("Preencha todos os campos!");
    }

    if (onEdit) {
      await axios.put(`http://localhost:8800/${onEdit.CodCli}`, {
        CodCli: clinica.CodCli.value,
        NomeCli: clinica.NomeCli.value,
        Endereco: clinica.Endereco.value,
        Telefone: clinica.Telefone.value,
        Email: clinica.Email.value
      })
      .then(({ data }) => toast.success(data))
      .catch(({ data }) => toast.error(data));
    }
  }


  return(
    <>
      <FormContainer ref={ref} onSubmit={handleSubmit}>
      <InputArea>
          <Label>CodCli</Label>
          <Input name="CodCli" />
        </InputArea>
        <InputArea>
          <Label>NomeCli</Label>
          <Input name="NomeCli" />
        </InputArea>
        <InputArea>
          <Label>Endereco</Label>
          <Input name="Endereco" />
        </InputArea>
        <InputArea>
          <Label>Telefone</Label>
          <Input name="Telefone" />
        </InputArea>
        <InputArea>
          <Label>Email</Label>
          <Input name="Email" />
        </InputArea>

        <Button type="submit">SALVAR</Button>

      </FormContainer>
    </>
  )
}
