import React from "react";

import { FaTrash, FaEdit } from "react-icons/fa";

import axios from "axios";

import { Table, Thead, Tr, Th, Tbody, Td } from "../styles/Grid";
import { toast } from "react-toastify";

export const Grid = ({ clinicas, setClinicas, setOnEdit }) => {

  const handleDelete = async (item) => {
    console.log(item.CodCli)
    await axios
    .delete(`http://localhost:8800/clinicas/${item.CodCli}`)
    // .then(({ data }) => {
    //   const newArray = clinicas.filter((item) => item.CodCli !== CodCli);

    //   setClinicas(newArray);
    //   toast.success(data);
    // })
    // .catch(({ data }) => toast.error(data));
    .then((response) => {
      console.log(response)
    })
    .catch((error) => {
      console.log(error)
    })
  }

  const handleEdit = async (item) => {
    setOnEdit(item);
  }

  return(
    <>
      <Table>
        <Thead>
          <Tr>
            <Th>Código</Th>
            <Th>Nome</Th>
            <Th>Endereço</Th>
            <Th onlyWeb>Telefone</Th>
            <Th>Email</Th>
            <Th></Th>
            <Th></Th>
          </Tr>
        </Thead>
        <Tbody>
          {clinicas.map((item, i) => 
            <Tr key={i}>
              <Td >{item.CodCli}</Td>
              <Td >{item.NomeCli}</Td>
              <Td >{item.Endereco}</Td>
              <Td  onlyWeb>{item.Telefone}</Td>
              <Td >{item.Email}</Td>
              <Td alighCenter >
                <FaEdit onClick={() => handleEdit(item)}/>
              </Td>
              <Td alighCenter >
                <FaTrash onClick={() => handleDelete(item)}/>
              </Td>
            </Tr>
          )}
        </Tbody>

      </Table>

    </>
  )
}
