import React from "react";

import { FaTrash, FaEdit } from "react-icons/fa";

import axios from "axios";

import { Table, Thead, Tr, Th, Tbody, Td } from "../styles/Grid";
import { toast } from "react-toastify";

export const Grid = ({ clinicas, setClinicas }) => {
  const handleDelete = async (CodCli) => {
    await axios
    .delete(`http://localhost:8800/${CodCli}`)
    .then(({ data }) => {
      const newArray = clinicas.filter((item) => item.CodCli !== CodCli);

      setClinicas(newArray);
      toast.success(data);
    })
    .catch(({ data }) => toast.error(data));
  }

  return(
    <>
      <Table>
        <Thead>
          <Tr>
            <Th>Código</Th>
            <Th>Nome</Th>
            <Th>Endereço</Th>
            <Th onlyWeb>Telefone</Th>
            <Th>Email</Th>
            <Th></Th>
            <Th></Th>
          </Tr>
        </Thead>
        <Tbody>
          {clinicas.map((item, i) => 
            <Tr key={i}>
              <Td >{item.CodCli}</Td>
              <Td >{item.NomeCli}</Td>
              <Td >{item.Endereco}</Td>
              <Td  onlyWeb>{item.Telefone}</Td>
              <Td >{item.Email}</Td>
              <Td alighCenter >
                <FaEdit/>
              </Td>
              <Td alighCenter >
                <FaTrash onClick={() => handleDelete(item.CodCli)}/>
              </Td>
            </Tr>
          )}
        </Tbody>

      </Table>

    </>
  )
}
