import React, { useRef, useEffect } from "react";

import axios from "axios";

import { toast } from "react-toastify";

import { FormContainer, InputArea, Input, Label, Button } from "../styles/Forms";

export const Form = ({ onEdit }) => {
  const ref = useRef();

  useEffect(() => {
    if (onEdit) {
      const clinica = ref.current;

      clinica.CodCli.value = onEdit.CodCli;
      clinica.NomeCli.value = onEdit.NomeCli;
      clinica.Endereco.value = onEdit.Endereco;
      clinica.Telefone.value = onEdit.Telefone;
      clinica.Email.value = onEdit.Email;
    }
  })

  const handleSubmit = async (event) => {
    event.preventDefault();

    const clinica = ref.current;
  }
  return(
    <>
      <FormContainer ref={ref} onSubmit={handleSubmit}>
      <InputArea>
          <Label>CodCli</Label>
          <Input name="CodCli" />
        </InputArea>
        <InputArea>
          <Label>NomeCli</Label>
          <Input name="NomeCli" />
        </InputArea>
        <InputArea>
          <Label>Endereco</Label>
          <Input name="Endereco" />
        </InputArea>
        <InputArea>
          <Label>Telefone</Label>
          <Input name="Telefone" />
        </InputArea>
        <InputArea>
          <Label>Email</Label>
          <Input name="Email" />
        </InputArea>

        <Button type="submit">SALVAR</Button>

      </FormContainer>
    </>
  )
}
