import React from "react";

import { FaTrash, FaEdit } from "react-icons/fa";

import axios from "axios";

import { Table, Thead, Tr, Th, Tbody, Td } from "../styles/Grid";
import { toast } from "react-toastify";

export const GridClinicaMedico = ({ clinicaMedico, setClinicaMedico, setOnEditCliMed }) => {

  const handleDelete = async (CodCli) => {
    console.log(CodCli)
    await axios
    .delete(`http://localhost:8800/clinicaMedico/${CodCli}`)
    .then(({ data }) => {
      const newArray = clinicaMedico.filter((item) => item.CodCli !== CodCli);

      setClinicaMedico(newArray);
      console.log(data);
    })
    .catch(({ data }) => console.log(data));
  }

  const handleEdit = async (item) => {
    setOnEditCliMed(item);
  }

  return(
    <>
      <Table>
        <Thead>
          <Tr>
            <Th>Código Clinica</Th>
            <Th>Código Médico</Th>
            <Th>Data Ingresso</Th>
            <Th onlyWeb>Carga Horaria Semanal</Th>
            <Th></Th>
            <Th></Th>
          </Tr>
        </Thead>
        <Tbody>
          {clinicaMedico.map((item, i) => 
            <Tr key={i}>
              <Td >{item.CodCli}</Td>
              <Td >{item.CodMed}</Td>
              <Td >{item.DataIngresso}</Td>
              <Td  onlyWeb>{item.CargaHorariaSemanal}</Td>
              <Td alighCenter >
                <FaEdit onClick={() => handleEdit(item)}/>
              </Td>
              <Td alighCenter >
                <FaTrash onClick={() => handleDelete(item.CodCli)}/>
              </Td>
            </Tr>
          )}
        </Tbody>

      </Table>

    </>
  )
}
