import React, { useState, useEffect } from "react";
import { toast, ToastContainer } from "react-toastify";
// import "react-toastify/dist/react-toastify";

import axios from "axios";

import { Container,Title } from "../styles/Home";

import { Form } from "../components/Forms";
import { Grid } from "../components/Grid";

export const Home = () => {
  const [clinicas, setClinicas] = useState([]);
  const [onEdit, setOnEdit] = useState(null);

  const getClinicas = async () => {
    try {
      const res = await axios.get("http://localhost:8800/clinicas");
      setClinicas(res.data);
    } catch (error) {
      toast.error(error);
    }
  }

  useEffect(() => {
    getClinicas();
  }, [setClinicas]);

  return(
    <>
      <Container>
        <Title>Cl√≠nicas</Title>

        <Form onEdit={onEdit} setOnEdit={setOnEdit} getClinicas={getClinicas}/>
        <Grid clinicas={clinicas} setClinicas={setClinicas} setOnEdit={setOnEdit}/>

      </Container>
      <ToastContainer autoClose={3000} position={toast.POSITION.BOTTOM_LEFT}/>

    </>
  )
}
